  DECLARE intID char(36);
  DECLARE strCompanyID char(36) DEFAULT '';

  SET intID = UUID();

  -- Kullanıcı doğrulama + company_id (UUID) alınması
  SELECT
    id,
    company_id INTO intUserID, strCompanyID
  FROM estatemaster.users
