      a.id,
      a.created_at,
      a.updated_at,
      a.created_by,
      a.name,
      a.surname,
      a.phone,
      a.state,
      a.userType,
      a.username,
      a.password,
      a.login_date,
      a.company_id,
      intID AS session_id
    FROM estatemaster.users AS a
    WHERE a.id = intUserID;
  END IF;
END
