main_block:
  BEGIN
    DECLARE current_state boolean DEFAULT TRUE;
    SET @oresult = '';

    -- Kayıt var mı + mevcut aktiflik durumu
    SELECT
      is_active INTO current_state
    FROM estatemaster.customers
    WHERE id = p_id;

    -- Eğer kayıt yoksa
