--
-- Create procedure `sp_create_customer`
--
CREATE
DEFINER = 'root'@'localhost'
PROCEDURE sp_create_customer (IN p_session_id char(36),
IN p_company_id char(36),
IN p_created_by char(36),
IN p_customer_type varchar(255),
IN p_assigned_user_id char(36),
IN p_first_name varchar(255),
IN p_last_name varchar(255),
IN p_email varchar(255),
IN p_phone varchar(25),
IN p_birth_date datetime,
IN p_gender varchar(10),
IN p_budget_min double,
IN p_budget_max double,
IN p_preferred_province varchar(255),
IN p_preferred_district varchar(255),
IN p_preferred_neighborhood varchar(255),
IN p_preferred_square_meters double,
IN p_preferred_room_count varchar(50),
IN p_amenities text,
IN p_facade varchar(100),
IN p_source varchar(100),
IN p_last_interaction datetime,
IN p_notes text)
main_block:
  BEGIN
    DECLARE intID char(36);
    DECLARE emailCount int DEFAULT 0;
    DECLARE companyId char(36);

    SET @oresult = '';
    SET @omessage = '';

    -- Oturum bilgilerini yükle
    CALL sp_load_session(p_session_id);
    SELECT
      @GlobalCompanyId INTO companyId;

    -- Aynı firma içinde e-posta zaten var mı?
    SELECT
      COUNT(*) INTO emailCount
    FROM estatemaster.customers
    WHERE email = p_email
    AND company_id = companyId;

    IF emailCount > 0 THEN
      SET @oresult = 'EMAIL_EXISTS';
      SET @omessage = CONCAT('Bu e-posta adresi zaten kayıtlı: ', p_email);
      SELECT
        @oresult AS oresult,
        @omessage AS message;
      LEAVE main_block;
    END IF;

    -- UUID oluştur, işlem başlat
    SET intID = UUID();
    START TRANSACTION;

      -- Müşteri kaydı
      INSERT INTO estatemaster.customers (id, created_at, created_by, company_id,
      customer_type, assigned_user_id,
      first_name, last_name, email, phone, birth_date,
      gender, budget_min, budget_max,
      preferred_province, preferred_district, preferred_neighborhood,
      preferred_square_meters, preferred_room_count,
      amenities, facade, source, last_interaction, notes)
        VALUES (intID, NOW(), p_created_by, companyId, p_customer_type, p_assigned_user_id, p_first_name, p_last_name, p_email, p_phone, p_birth_date, p_gender, p_budget_min, p_budget_max, p_preferred_province, p_preferred_district, p_preferred_neighborhood, p_preferred_square_meters, p_preferred_room_count, p_amenities, p_facade, p_source, p_last_interaction, p_notes);

      SET @oresult = intID;
      SET @omessage = 'Müşteri başarıyla oluşturuldu.';
      SELECT
        @oresult AS oresult,
        @omessage AS message;
    COMMIT;
  END
  $$

